mapper.select:
SELECT COUNT(*) > 0
FROM seat_table
WHERE res_idx = #{resIdx} AND type = #{type} AND name = #{name}

SELECT res_idx
FROM restaurant
WHERE user_idx = #{userIdx}

SELECT user_idx, res_idx,  res_name, res_call, res_address1, res_address2, res_introduce, res_reserve_time, res_image1, res_image2, res_image3
from restaurant
WHERE user_idx = #{userIdx}

mapper.update:
UPDATE seat_table
SET x = #{x},
y = #{y},
res_idx = #{resIdx}
WHERE seat_id = #{seatId}
